import IconTextButton from './IconTextButton'
import Typography from '@material-ui/core/Typography';
import Grid from  '@material-ui/core/Grid';
import thumbsup from './Icons/thumbs-up.png'


export default function InfoPanelOne({handleButton, buttonState}) {
  return (
    <>
    <div className="component-space">
      <Typography variant="h6" gutterBottom>
        Have you ever visited Dubai?
      </Typography>
      <Grid container spacing="3">
        {["No", "Yes"].map((tag, idx) => (
          <Grid item >
            <img className = "img" src={thumbsup}/>
            <IconTextButton key={idx} tag={tag}  handleButton={handleButton} pageNumber="0" buttonState={buttonState}> {/*pagenumbers are the button indexes in the state array of the parent */}
            </IconTextButton>
          </Grid>
        ))}
      </Grid>
    </div>

    <div className="component-space">
      <Typography variant="h6" gutterBottom>
        Do you have any friends or family in Dubai?
      </Typography>
      <Grid container spacing="3">
        {["No", "Yes"].map((tag, idx) => (
          <Grid item >
            <IconTextButton key={idx} tag={tag}  handleButton={handleButton} pageNumber="7" buttonState={buttonState}/> {/*pagenumbers are the button indexes in the state array of the parent */}
          </Grid>
        ))}
      </Grid>
    </div>

    <div className="component-space">
      <Typography variant="h6" gutterBottom>
        How long do you want to stay in Dubai?
      </Typography>
      <Grid container spacing="3">
        {["Upto 3 months", "3 to 6 months", "6 to 12 months", "+12 months", "+3 years"].map((tag, idx) => (
          <Grid item >
            <IconTextButton key={idx} tag={tag}  handleButton={handleButton} pageNumber="8" buttonState={buttonState}/> {/*pagenumbers are the button indexes in the state array of the parent */}
          </Grid>
        ))}
      </Grid>
    </div>
    
    </>
  );
}
