import React, {useState} from 'react';
import './style.css'

export default function IconTextButton({tag, handleButton, pageNumber}) {
    const [buttonState, setButtonState] = useState(false);

    const handleChange = () => {
        handleButton(pageNumber, tag)
        if(buttonState == tag) setButtonState(true)
    }
    
    return (
        <button 
        onClick={handleChange}
        style={buttonState? {background: "linear-gradient(125deg, #5132e2 0%, #6a2ee5 50%, #8329e9 100%)", color:"white"} : {}} 
        className="icon-text-btn">{tag}</button>
    );
  }
