import React from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from  '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,

      },
    gridMargin:
    {   
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(2),
    },

    container: {
        display: 'flex',
        flexWrap: 'wrap',
      },


}));

const GreenCheckbox = withStyles({
    root: {
      color: "green",
      '&$checked': {
        color: "blue",
      },
    },
    checked: {},
  })((props) => <Checkbox color="default" {...props} />);



export default function UserInfoPage() {
    const classes = useStyles();

    const [state, setState] = React.useState({
        checkedMale: false,
        checkedFemale: false,
      });

      const handleChange = (event) => {
        state.checkedFemale = false;
        state.checkedMale = false;
        setState({ ...state, [event.target.name]: event.target.checked });
      };
  return (
    <React.Fragment>
         <Grid container spacing="3" justify ="center" >
            <Grid item xs= {6}>
              {/* ADD BACKGOUND */}
            </Grid>
            <Grid item xs= {6}>
            <div className={classes.root}>
                <Grid item xs={12}>
                    <Typography component="h1" variant="h4" align="center">
                        Apply now to work in Dubai
                    </Typography>
                </Grid>

                <Grid container spacing={1} className={classes.gridMargin}> 
                    <Grid item xs={6} >
                        <TextField
                            id="outlined-firstname-input"
                            label="First Name"
                            type="name"
                            autoComplete="current-firstname"
                            variant="outlined"
                            fullWidth
                        />
                    </Grid>
                    <Grid item xs={6} > 
                            <TextField
                            id="outlined-firstname-input"
                            label="First Name"
                            type="name"
                            autoComplete="current-firstname"
                            variant="outlined"
                            fullWidth 
                        />
                     </Grid>
                </Grid>

                <Grid container spacing={1} className={classes.gridMargin}> 
                    <Grid item xs={6} className={classes.container}>
                        <TextField
                                id="date"
                                label="Date of Birthday"
                                type="date"
                                variant="outlined"
                                defaultValue=""
                                fullWidth 
                        
                                InputLabelProps={{
                                shrink: true,
                                }}
                        />
                    </Grid>
                    <Grid item xs={6} > 
                     <p style= {{margin:0}}>Gender</p>
                        <FormGroup row>
                            <FormControlLabel
                                control={<GreenCheckbox checked={state.checkedMale } onChange={handleChange} name="checkedMale" />}
                                label="Male"
                            />
                            <FormControlLabel
                                control={<GreenCheckbox checked={state.checkedFemale} onChange={handleChange} name="checkedFemale" />}
                                label="Female"
                            />
                        </FormGroup>
                     </Grid>
                </Grid>
            

            </div>
        </Grid>

          </Grid>
      
    </React.Fragment>
  );
}
