import React, { useEffect, useState } from 'react';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import InfoPanelOne from './InfoPanelOne';
import UserInfoPage from './UserInfoPage';
import PaymentForm from './PaymentForm';
import Review from './Review';
import LinearProgress from '@material-ui/core/LinearProgress';
import './style.css'

const useStyles = makeStyles((theme) => ({
  stepper: {
    padding: theme.spacing(3, 0, 5),
  },

  buttons: {
    display: 'flex',
  },
  
  root: {
    flexGrow: 1,
  },

  mainlayout:{
    margin:0,
    padding: 0,
    clear: "both",
    display: "block",
  },
  header:{
    display: "flex",
    justifyContent: "center"
  },

}));

const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
      width: "70%",
    },
    bar: {
      width: "fit-content",
      borderRadius: 5,
      background: "linear-gradient(125deg, #5132e2 0%, #6a2ee5 50%, #8329e9 100%)",
    
    },
  }))(LinearProgress);

const steps = ['User Info', 'Travel Info panel 1', 'Travel Info panel 2', 
              'Travel Info panel 3' , 'Travel Info panel 4' , 'Travel Info panel 5' , 
              'Travel Info panel 6' , 'Review'];

function getStepContent(step) {
  switch (step) {
    case 0:
      return <UserInfoPage />;
    case 1:
      return <InfoPanelOne />;
    case 2:
      return <Review />;
    default:
      throw new Error('Unknown step');
  }
}





export default function Checkout() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);

  const [progress, setprogress] = useState(0)
  const handleNext = () => {
    setActiveStep(activeStep + 1);
    setprogress(progress + 10);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
    setprogress(progress - 10);
  };

  return (
    <>

      <main className={classes.mainlayout}>

      <div className={classes.header}>
        <Typography component="h1" variant="h4" align="center">
          digg
        </Typography>

        {activeStep === 0 ? <> </> : 
          <div className={classes.root}>
              <BorderLinearProgress variant="determinate" value={progress} />
          </div>}
      </div>

          <>
            {activeStep === steps.length ? (
              <>
                <Typography variant="h5" gutterBottom>
                  Thank you for your order.
                </Typography>
                <Typography variant="subtitle1">
                  Your order number is #2001539. We have emailed your order confirmation, and will
                  send you an update when your order has shipped.
                </Typography>
              </>
            ) : (
              <>
                {getStepContent(activeStep)}
                <div className={classes.buttons}>
                  {activeStep !== 0 && (
                    <button 
                    className="back-btn"
                    style={{marginLeft:"10px"}}
                    onClick={handleBack}>
                      Back
                    </button>
                  )}
                  <button
                    className="next-btn"
                    onClick={handleNext}
                    style={{marginLeft:"10px"}}
                  >
                    {activeStep === steps.length - 1 ? 'Complete Application' : 'Next'}
                  </button>
                </div>
              </>
            )}
          </>
      </main>
    </>
  );
}
