import React, { useEffect, useState } from 'react';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import InfoPanelOne from './InfoPanelOne';
import PaymentForm from './PaymentForm';
import Review from './Review';
import LinearProgress from '@material-ui/core/LinearProgress';
import './style.css'

const useStyles = makeStyles((theme) => ({

  layout: {



  },
  
  stepper: {
    padding: theme.spacing(3, 0, 5),
  },

  buttons: {
    display: 'flex',
    justifyContent: 'flex-end',
  },

  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1),
  },

  root: {
    flexGrow: 1,
  },

  mainlayout:{
    margin:0,
    padding: 0,
    clear: "both",
    display: "block",
  }
}));

const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
    },
    bar: {
      borderRadius: 5,
      background: "linear-gradient(90deg, #311b92 0%, #4527a0 10%, #512da8, 20%, #5e35b1 30%, #673ab7 60%, #7e57c2 100%)",
    
    },
  }))(LinearProgress);

const steps = ['User Info', 'Travel Info panel 1', 'Travel Info panel 2', 
              'Travel Info panel 3' , 'Travel Info panel 4' , 'Travel Info panel 5' , 
              'Travel Info panel 6' , 'Review'];

function getStepContent(step) {
  switch (step) {
    case 0:
      return <InfoPanelOne />;
    case 1:
      return <PaymentForm />;
    case 2:
      return <Review />;
    default:
      throw new Error('Unknown step');
  }
}





export default function Checkout() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);

  const [progress, setprogress] = useState(0)
  const handleNext = () => {
    setActiveStep(activeStep + 1);
    setprogress(progress + 10);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
    setprogress(progress - 10);
  };

  return (
    <React.Fragment>

      <main className={classes.layout}>
          <Typography component="h1" variant="h4" align="center">
            digg
          </Typography>
          <Grid container spacing="3" className={classes.mainlayout}>
          <Grid item xs= {6}>
               <Typography component="h1" variant="h4" align="center">
                Apply now
              </Typography>
            </Grid>
            <Grid item xs= {6}>
               <Typography component="h1" variant="h4" align="center">
                Apply now
              </Typography>
            </Grid>

          </Grid>
          

          {activeStep === 0 ? 
               <React.Fragment>
              </React.Fragment>
             : (
            <React.Fragment>
              <div className={classes.root}>
                  <BorderLinearProgress variant="determinate" value={progress} />
              </div>
            </React.Fragment>)}

          <React.Fragment>
            {activeStep === steps.length ? (
              <React.Fragment>
                <Typography variant="h5" gutterBottom>
                  Thank you for your order.
                </Typography>
                <Typography variant="subtitle1">
                  Your order number is #2001539. We have emailed your order confirmation, and will
                  send you an update when your order has shipped.
                </Typography>
              </React.Fragment>
            ) : (
              <React.Fragment>
                {getStepContent(activeStep)}
                <div className={classes.buttons}>
                  {activeStep !== 0 && (
                    <button onClick={handleBack}>
                      Back
                    </button>
                  )}
                  <button
                    onClick={handleNext}
                  >
                    {activeStep === steps.length - 1 ? 'Place order' : 'Next'}
                  </button>
                </div>
              </React.Fragment>
            )}
          </React.Fragment>
      </main>
    </React.Fragment>
  );
}
