import React, { useEffect, useState } from 'react';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import InfoPanelOne from './InfoPanelOne';
import PaymentForm from './PaymentForm';
import Review from './Review';
import LinearProgress from '@material-ui/core/LinearProgress';


const useStyles = makeStyles((theme) => ({

  layout: {
    width: 'auto',
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
      width: 600,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  stepper: {
    padding: theme.spacing(3, 0, 5),
  },
  buttons: {
    display: 'flex',
    justifyContent: 'flex-end',
  },
  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1),
  },
  root: {
    flexGrow: 1,
  },
}));

const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
    },
    bar: {
      borderRadius: 5,
      background: "linear-gradient(5deg, #311b92 0%, #4527a0 10%, #512da8, 20%, #5e35b1 30%, #673ab7 60%, #7e57c2 100%)",
    
    },
  }))(LinearProgress);

const steps = ['Shipping address', 'Travel Info panel 1', 'Travel Info panel 2', 'Travel Info panel 3' , 'Travel Info panel 4' , 'Travel Info panel 5'];

function getStepContent(step) {
  switch (step) {
    case 0:
      return <AddressForm />;
    case 1:
      return <PaymentForm />;
    case 2:
      return <Review />;
    default:
      throw new Error('Unknown step');
  }
}





export default function Checkout() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);

  const [progress, setprogress] = useState(0)
  const handleNext = () => {
    setActiveStep(activeStep + 1);
    setprogress(progress + 10);
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
    setprogress(progress - 10);
  };

  return (
    <React.Fragment>

      <main className={classes.layout}>
          <Typography component="h1" variant="h4" align="center">
            Checkout
          </Typography>
          

          {activeStep === 0 ? 
               <React.Fragment>
              </React.Fragment>
             : (
            <React.Fragment>
              <div className={classes.root}>
                  <BorderLinearProgress variant="determinate" value={progress} />
              </div>
            </React.Fragment>)}

          <React.Fragment>
            {activeStep === steps.length ? (
              <React.Fragment>
                <Typography variant="h5" gutterBottom>
                  Thank you for your order.
                </Typography>
                <Typography variant="subtitle1">
                  Your order number is #2001539. We have emailed your order confirmation, and will
                  send you an update when your order has shipped.
                </Typography>
              </React.Fragment>
            ) : (
              <React.Fragment>
                {getStepContent(activeStep)}
                <div className={classes.buttons}>
                  {activeStep !== 0 && (
                    <Button onClick={handleBack} className={classes.button}>
                      Back
                    </Button>
                  )}
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === steps.length - 1 ? 'Place order' : 'Next'}
                  </Button>
                </div>
              </React.Fragment>
            )}
          </React.Fragment>
      </main>
    </React.Fragment>
  );
}
